import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

function App(){
  return(
    <Router>
      <div className="container">
        <h1>Menu</h1>
        <hr/>
        <Switch>
          <Route>
            Departamentos
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;




