import logo from '.././logo.svg';
import '../App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Outlet, Link } from "react-router-dom";
import {Container, FormGroup, Table, Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';

import Menu from "../menu/menu";

import Swal from 'sweetalert2';
import 'jquery/dist/jquery.min.js';
import 'datatables.net-dt/css/jquery.dataTables.min.css';
import 'datatables.net-dt/js/dataTables.dataTables.min.js';
import $ from 'jquery';

class App extends React.Component {

  componentDidMount(){
    this.cargarTabla();
  }


  //Seteamos un estado de la modal por defecto
  state={
    modalInsertar: false
  }

  //esta funcion cambia el estado a true
  modalInsertar=()=>{
    this.setState({
      modalInsertar:true,
    });
  }

  //esta funcion cierra la modal
  cerrarModalInsertar=()=>{
    this.setState({
      modalInsertar:false
      
    })
  }
  onInsertPerfil=()=>{
    var nomDep = document.getElementById("nomDep").value;
    if (nomDep == "") {
      Swal.fire("Campos vacios","Debes ingresar los datos solicitados","error")
    }else{
      //headers de la solicitud
      var myHeaders = new Headers();
      myHeaders.append("Content-Type","application/json")
      //armar body de la solicitud
      var raw = JSON.stringify({
        "nombre":nomDep,
        "activo":1,
        "usuario":1
      });
    }
    //options del request
    var requestOptions={
      method:"POST",
      headers:myHeaders,
      body:raw,
      redirect:"follow"
    };
    fetch('https://localhost:7218/api/InsertPerfil',requestOptions)
    .then(response=>response.text())
    .then(result=>console.log(result))
    .catch(error=>console.log('error', error));
    this.cerrarModalInsertar();
    Swal.fire(
      {
        title:'Se ha agregado un uevo registro',
        icon:'success',
        showConfirmButton: false,
        timer: 1500
      }
  )
    $('#lista_perfiles').DataTable().ajax.reload();
    /*let depInfo={
       nombre:this.refs.nomDep.value

    };

    console.log(depInfo);

    fetch('http://localhost:5218/api/InsertPerfil',{
      method:"POST",
      headers:{'Content-type':'application/json'},
      body:depInfo
    }).then(r=>r.json()).then(res=>{
      if(res){
        this.state({message:"Registro creado"});
      }
    }) //verificar endpoint*/
  }




  cargarTabla=()=>{ {/* funcion para traer los datos de la tabla con datatable */}
    $('#lista_perfiles').dataTable().fnDestroy();
    $('#lista_perfiles').dataTable({
      "language":{
        "url":""
      },
      "dom":"Bfrtip",
      "ajax":{
        "url":"https://localhost:7218/api/GetPerfiles",
        "method":"POST",
        "timeout":0,
        "contentType":"application/json",
        "dataSrc":"response.data",
        data:function(d){
          return JSON.stringify({"activo":1});
        },
        dataType:"json"
      },

      "columns":[
        {
          "data":"Id",
        },
        {
          "data":"Nombre"
        },
        {
          "data":"FechaHora"
        },
        {
          "data":"Activo"
        },
        {
          "data":"Usuario"
        },
        // {
        //   "data":"Activo"
        // },
        // {
        //   "data":"Usuario"
        // },
      ]
    });

  }
  render(){
    return (
      <>
      <Menu/>

      <Outlet />

        <Container>
          <h1>Administraci√≥n de Perfiles</h1>
          <br/>
          <Button color='success' onClick={()=>this.modalInsertar()}>Nuevo Departamento</Button>
          <br/><br/>
          <Table className="table-bordered" id="lista_perfiles">
            <thead>
              <tr align="center">
                <th width="10%">Id</th>
                <th>Nombre</th>
                <th width="20%">Fecha</th>
                <th width="10%">Status</th>
                <th width="12%">Usuario</th>
                {/* <th width="10%">Status</th>
                <th width="12%">Usuario</th> */}
              </tr>
            </thead>
          </Table>
          <Modal isOpen={this.state.modalInsertar}>
            <ModalHeader>
              <div>
                <h3>Perfiles | Nuevo Registro</h3>
              </div>
            </ModalHeader>
            <ModalBody>
              <FormGroup>
                <label>Nombre:</label>
                <input type="text" className="form-control" ref="nomDep"  name="nombre" />
              </FormGroup>
            </ModalBody>      
            <ModalFooter>
              <Button color="primary" onClick={()=>this.onInsertPerfil()}>Guardar Registro</Button>
              <Button color='danger' onClick={()=>this.cerrarModalInsertar()}>Cancelar</Button>
            </ModalFooter>
          </Modal>
        </Container>
      </>
    );
  }
}

export default App;
