import logo from './logo.svg';
import './App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, FormGroup, Table, Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';

class App extends React.Component {
  //Seteamos un estado de la modal por defecto
  state={
    modalInsertar: false
  }

  //esta funcion cambia el estado a true
  modalInsertar=()=>{
    this.setState({
      modalInsertar:true,
    });
  }

  //esta funcion cierra la modal
  cerrarModalInsertar=()=>{
    this.setState({
      modalInsertar:false
    })
  }
  render(){
    return (
      <>
        <Container>
          <h1>Administraci√≥n de Departamentos</h1>
          <br/>
          <Button color='danger' onClick={()=>this.modalInsertar()}>Nuevo Departamento</Button>
          <br/><br/>
          <Table className="table-bordered">
            <thead>
              <tr align="center">
                <th width="10%">Id</th>
                <th>Nombre</th>
                <th width="20%">Registro</th>
                <th width="10%">Status</th>
                <th width="12%">Usuario</th>
              </tr>
            </thead>
          </Table>
          <Modal isOpen={this.state.modalInsertar}>
            <ModalHeader>
              <div>
                <h3>Departamentos|Nuevo Registro</h3>
              </div>
            </ModalHeader>
            <ModalBody>
              <FormGroup>
                <label>Id:</label>
                <input type="text" className="form-control" readOnly name="id"/>
              </FormGroup>
              <FormGroup>
                <label>Nombre:</label>
                <input type="text" className="form-control" readOnly name="nombre" />
              </FormGroup>
            </ModalBody>      
            <ModalFooter>
              <Button color="primary">Guardar Registro</Button>
              <Button color='danger'></Button>
            </ModalFooter>
          </Modal>
        </Container>
      </>
    );
  }
}

export default App;
