import logo from './logo.svg';
import './App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, FormGroup, Table, Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';

class App extends React.Component {
  //estado por defecto del modal
  state={
    modalInsertar: false,
  }
  mostrarModalInsertar=()=>{
    //esta funcion cambia el estado del modal a true
    this.setState({
      modalInsertar:true,
    });
  };
  //esta funcion cuierra el modal
  cerrarModalInsert=()=>{
    this.setState({
      modalInsertar:false,
    })
  }
  onInsertDepto=()=>{
    let depInfo={
      nombre:this.refs.nomDep.value
    };
    fetch('https://localhost:7218/api/InsertDepartamento',{
      method:"POST",
      headers: {'Content-type':'application/json'},
      body:depInfo

    }).then(r=>r.json()).then(res=>{
      if(res){
        this.state({message:"Registro creado"});
      }
    });
  }

  render(){
    return (
      <>
        <Container>
          <h4>Administracion de Departamentos</h4>
          <Button color='success' onClick={()=>this.mostrarModalInsertar()}>Nuevo Departamento</Button>
          <br></br>
          <Table className="table-bordered">
            <thead>
              <tr align="center">
                <th width="10%">Id</th>
                <th>Nombre</th>
                <th width="20%">Registro</th>
                <th width="10%">Status</th>
                <th width="12%">Usuario</th>
              </tr>
            </thead>
          </Table>
          <Modal isOpen={this.state.modalInsertar}>
            <ModalHeader>
              <div>
                <h3>Departamentos|Nuevo Registro</h3>
              </div>
            </ModalHeader>
            <ModalBody>
              <FormGroup>
                <label>Nombre:</label>
                <input type="text" name='nombre' ref='nomDep' className='form-control' />
              </FormGroup>
            </ModalBody>
            <ModalFooter>
              <Button color='primary' onClick={()=>this.onInsertDepto()}>Guardar Registro</Button>
              <Button color='danger' onClick={()=>this.cerrarModalInsert()}>Cancelar</Button>
            </ModalFooter>
          </Modal>
        </Container>
      </>
    );
  }
}

export default App;
