import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Routes,
  Route,
  Link
} from "react-router-dom";

function App(){
  return(
    <Router>
      <div className="container">
        <div className="btn-group">
          <Link to="/" clasname="btn btn-dark">
          </Link>
        </div>
        <hr />
        <Routes>
          {/* <Route path="/Departamentos" exact>
            <Departamentos/>
          </Route> */}

        </Routes>
        <Routes>
          <Route path="/Empleados" element={<h1>Pagina de Empleados</h1>}/>
        </Routes>
        <Routes>
          <Route path="/Perfiles" element={<h1>Pagina de Perfiles</h1>}/>
        </Routes>
        <Routes>
          <Route path="/Personas" element={<h1>Pagina de Personas</h1>}/>
        </Routes>
        <Routes>
          <Route path="/Usuarios" element={<h1>Pagina de Usuarios</h1>}/>
        </Routes>
      </div>
    </Router>
  );
}

export default App;




